mapper.select:
select count(*) from system_user
where user_name = #{userName}

    and phone = #{phone}

select e.*,s.user_name userName from evaluate_inner e
LEFT JOIN system_user s on s.id = e.user_id
where goods_id = #{goodsId}

select * from goods g


        and g.name like CONCAT('%',#{name},'%')


        and g.area = #{area}
